import { Box, Button, Card, CardContent, TextField, Typography } from "@mui/material";
import React, { useState } from "react";
import Center from "./Center";
import useForm from "../customhooks/useForm";
import { createAPIEndpoint } from "../api";

const getFreshModel= () =>({
    name:'',
    email:''
})


export default function Login() {

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange
    } = useForm(getFreshModel);

    const login = e =>{
        e.preventDefault();
        if (validate()) {
            createAPIEndpoint('User')
        }
    }

    const validate = () => {
        let temp = {}
        temp.email =  (/\S+@\S+\.\S+/).test(values.email) ? "" : "This field can not be empty."
        temp.name = values.name != "" ? "" : "This field can not be empty."
        setErrors(temp)
        return Object.values(temp).every(x=>x === "")
    }
    return (   
        <Center>
            <Card sx={{ width:400}}>
            <CardContent sx={{textAlign:'center'}}>
                <Typography variant="h3" sx={{my:3}}>Quiz App</Typography>
                <Box sx={{
                    '& .MuiTextField-root':{
                        m:1,
                        width:'90%',
                    }
                    }}>
                    <form noValidate autoComplete="off" onSubmit={login}>
                        <TextField 
                        label="Email"
                        name="email"
                        value={values.email}
                        onChange={handleInputChange}
                        variant="outlined"
                        {...(errors.email && {error:true, helperText:errors.email})}
                        />
                        <TextField 
                        label="Name"
                        name="name"
                        value={values.name}
                        onChange={handleInputChange}
                        variant="outlined"
                        {...(errors.name && {error:true, helperText:errors.name})}
                        />
                        <Button 
                        type ="submit"
                        variant="contained"
                        size="large"
                        sx={{width:'90%', m:1}}>Start</Button>
                    </form>
                </Box>
            </CardContent>
            </Card>
        </Center>
    )
}
